frappe.ui.form.on('Examination Marks Entry', {
	refresh(frm) {
		var admissionNumbers = frm.doc.student_marks.map(function(row) {
            return row.admission_number;
        });
        console.log(frm.doc.form)

        // Set query filter for the admission_number field
        frm.fields_dict.student_marks.grid.get_field('admission_number').get_query = function(doc) {
            return {
                filters: {
                    'name': ['not in', admissionNumbers],
                    'student_group': frm.doc.form
                }
            };
        };
	}
})

frappe.ui.form.on('Student Marks', {
	refresh: function(frm, cdt, cdn) {
		frm.set_query('admission_number', function() {
            return {
                filters: {
                    'status': 1
                }
            };
        });
	},
	status: function(frm, cdt, cdn) {
	    let item = locals[cdt][cdn];
        frappe.call({
			method: 'cbigdl_education.cbigdl_education.doctype.examination_marks_entry.examination_marks_entry.get_grade',
			args: {
				'cat_marks':item.cat_marks,
				'exam_marks':item.exam_marks,
				'grade_format':frm.doc.grade_format
			},
			callback: function(r) {
				let student_name=r.message
				frappe.model.set_value(cdt,cdn,"total_marks",student_name[1])
				frappe.model.set_value(cdt,cdn,"grade",student_name[0])
			}
		});
    },
    cat_marks: function(frm, cdt, cdn) {
        let item = locals[cdt][cdn];
        let catMarks = item.cat_marks;

        if (catMarks > 100) {
            frappe.model.set_value(cdt, cdn, 'cat_marks', 0);
            frappe.msgprint('The Cat Marks value is greater than 100. Setting it to 0.');
        }
    },
    exam_marks: function(frm, cdt, cdn) {
        let item = locals[cdt][cdn];
        let examMarks = item.cat_marks;

        if (examMarks > 70) {
            frappe.model.set_value(cdt, cdn, 'exam_marks', 0);
            frappe.msgprint('The Exam Marks value is greater than 70. Setting it to 0.');
        }
    }
});

