frappe.ui.form.on('Grade Format', {

	max_scale:function(frm, cdt, cdn){
	    let item = locals[cdt][cdn];
		if (item.max_scale > 100) {
			frappe.model.set_value(cdt, cdn, 'max_scale', 100);
			frappe.throw('Maximum scale cannot be more than 100.');
		}
		let row = frm.doc.grade_formats.find(row => row.name === item.name);

		if (row && item.min_scale <= row.min_scale) {
			frappe.model.set_value(cdt, cdn, 'max_scale', row.min_scale - 1);
			frappe.throw('Maximum scale must be less than the next row\'s minimum scale minus 1.');
		}
	},
		min_scale:function(frm, cdt, cdn){
    	    let item = locals[cdt][cdn];
    		if (item.min_scale < 0) {
    			frappe.model.set_value(cdt, cdn, 'min_scale', 0);
    			frappe.throw('Minimum scale cannot be less than 0.');
    		}

	}
})

