})

frappe.ui.form.on('Student Marks', {
	refresh(frm) {

	},
	status: function(frm, cdt, cdn) {
	    let item = locals[cdt][cdn];
        frappe.call({
			method: 'cbigdl_education.cbigdl_education.doctype.examination_marks_entry.examination_marks_entry.get_grade',
			args: {
				'cat_marks':item.cat_marks,
				'exam_marks':item.exam_marks,
				'grade_format':frm.doc.grade_format
			},
			callback: function(r) {
				let student_name=r.message
				frappe.model.set_value(cdt,cdn,"total_marks",student_name[1])
				frappe.model.set_value(cdt,cdn,"grade",student_name[0])
			}
		});
    }
});
